// Initial Test for Rubber Duck.  YayYay

TEST(RubberDuckTest,Constructor){
  RubberDuck my_ralph;
  string except_str1_1 = "Cannot fly.";
  EXPECT_EQ(except_str1_1,my_ralph.performFly()) << "FAIL: performFly" ;

  string except_str1_2 = "Quack at 2 decibels.";
  EXPECT_EQ(except_str1_2,my_ralph.performQuack()) << "FAIL: performQuack" ;

  Quack q;
  my_ralph.setQuackBehavior(&q);
  string except_str1_3 = "Quack at 10 decibels.";
  EXPECT_EQ(except_str1_3,my_ralph.performQuack()) << "FAIL: performQuack of setQuackBehavior" ;

  FlyWithWings f;
  my_ralph.setFlyBehavior(&f);
  string except_str1_4 = "Fly with wings at speed of 5 mph.";
  EXPECT_EQ(except_str1_4,my_ralph.performFly()) << "FAIL: performFly of setFlyBehavior" ;
}

TEST(RubberDuckTest,display){
  RubberDuck my_ralph1;
  string except_str1_5="I am a Rubber Duck.";
  EXPECT_EQ(except_str1_5,my_ralph1.display()) << "FAIL: display" ;
}
