TEST(MallardTest,Constructor){
  Mallard my_ralph2;
  string except_str2_1 = "Fly with wings at speed of 5 mph.";
  EXPECT_EQ(except_str2_1,my_ralph2.performFly()) << "FAIL: performFly" ;

  string except_str2_2 = "Quack at 10 decibels.";
  EXPECT_EQ(except_str2_2,my_ralph2.performQuack()) << "FAIL: performQuack" ;

  Squeak s(2);
  my_ralph2.setQuackBehavior(&s);
  string except_str2_3 = "Quack at 2 decibels.";
  EXPECT_EQ(except_str2_3,my_ralph2.performQuack()) << "FAIL: performQuack of setQuackBehavior" ;

  NoFly n;
  my_ralph2.setFlyBehavior(&n);
  string except_str2_4 = "Cannot fly.";
  EXPECT_EQ(except_str2_4,my_ralph2.performFly()) << "FAIL: performFly of setFlyBehavior" ;
}

TEST(MallardTest,display){
  Mallard my_ralph3;
  string except_str2_5="I am a Mallard.";
  EXPECT_EQ(except_str2_5,my_ralph3.display()) << "FAIL: display" ;
} 
